@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@-moz-document domain("wiki.archlinux.org") {
  :root {
    @media (prefers-color-scheme: light) {
      #catppuccin(@lightFlavor);
    }
    @media (prefers-color-scheme: dark) {
      #catppuccin(@darkFlavor);
    }
  }

  #catppuccin(@flavor) {
    @rosewater: @catppuccin[@@flavor][@rosewater];
    @flamingo: @catppuccin[@@flavor][@flamingo];
    @pink: @catppuccin[@@flavor][@pink];
    @mauve: @catppuccin[@@flavor][@mauve];
    @red: @catppuccin[@@flavor][@red];
    @maroon: @catppuccin[@@flavor][@maroon];
    @peach: @catppuccin[@@flavor][@peach];
    @yellow: @catppuccin[@@flavor][@yellow];
    @green: @catppuccin[@@flavor][@green];
    @teal: @catppuccin[@@flavor][@teal];
    @sky: @catppuccin[@@flavor][@sky];
    @sapphire: @catppuccin[@@flavor][@sapphire];
    @blue: @catppuccin[@@flavor][@blue];
    @lavender: @catppuccin[@@flavor][@lavender];
    @text: @catppuccin[@@flavor][@text];
    @subtext1: @catppuccin[@@flavor][@subtext1];
    @subtext0: @catppuccin[@@flavor][@subtext0];
    @overlay2: @catppuccin[@@flavor][@overlay2];
    @overlay1: @catppuccin[@@flavor][@overlay1];
    @overlay0: @catppuccin[@@flavor][@overlay0];
    @surface2: @catppuccin[@@flavor][@surface2];
    @surface1: @catppuccin[@@flavor][@surface1];
    @surface0: @catppuccin[@@flavor][@surface0];
    @base: @catppuccin[@@flavor][@base];
    @mantle: @catppuccin[@@flavor][@mantle];
    @crust: @catppuccin[@@flavor][@crust];
    @accent: @catppuccin[@@flavor][@@accentColor];

    color-scheme: if(@flavor = latte, light, dark);

    ::selection {
      background-color: fade(@accent, 30%);
    }

    input,
    textarea {
      &::placeholder {
        color: @subtext0 !important;
      }
    }

    #content table,
    #content h1,
    #content h2,
    #content h3,
    #content h4,
    #content h5,
    #content pre,
    #content code,
    #content tt {
      color: @text !important;
    }

    body.skin-vector-2022 .vector-sticky-header {
      color: @text !important;
      background-color: @mantle !important;
      border-bottom-color: @blue !important;
    }

    #vector-main-menu-pinned-container,
    #vector-page-tools-pinned-container {
      background-color: @base !important;
    }

    #ooui-php-191,
    #preferences .mw-htmlform-submit-buttons {
      background-color: @mantle !important;
    }

    .mw-echo-ui-notificationItemWidget-content-message-header,
    .oo-ui-labelElement-label {
      color: @text !important;
    }

    .oo-ui-optionWidget-selected {
      background-color: @surface2 !important;
    }

    .oo-ui-buttonElement-framed.oo-ui-widget-disabled
      > .oo-ui-buttonElement-button {
      background-color: @surface0 !important;
      color: @text !important;
      border-color: @surface2 !important;
      filter: brightness(0.4);
      cursor: not-allowed;
    }

    .mw-htmlform-ooui .mw-htmlform-matrix tbody tr:nth-child(even) td {
      background-color: @surface1 !important;
    }

    .mw-htmlform-ooui .mw-htmlform-matrix tbody tr:hover td {
      background-color: @surface2 !important;
    }

    .mw-htmlform-ooui .mw-htmlform-matrix tbody tr:nth-child(odd) td,
    .mw-rcfilters-ui-filterTagMultiselectWidget.oo-ui-widget-enabled
      .oo-ui-tagMultiselectWidget-handle,
    .oo-ui-tagMultiselectWidget.oo-ui-widget-enabled.oo-ui-tagMultiselectWidget-outlined
      .oo-ui-tagItemWidget.oo-ui-widget-enabled,
    .mw-rcfilters-ui-cell.mw-rcfilters-ui-filterTagMultiselectWidget-views-select,
    .mw-rcfilters-ui-changesListWrapperWidget .mw-changeslist-legend,
    .oo-ui-popupWidget-popup,
    .mw-echo-ui-notificationItemWidget,
    .mw-echo-ui-placeholderItemWidget {
      background-color: @surface0 !important;
    }

    .mw-echo-ui-notificationsInboxWidget-toolbarWrapper {
      background-color: @base !important;
    }

    #archnavbar {
      background-color: @mantle !important;
      border-bottom-color: @blue !important;
    }
    .mw-parser-output {
      background-color: @base !important;
    }

    .mw-page-container {
      background-color: @base !important;
    }

    .vector-header-container {
      background-color: @base !important;
    }

    #content,
    header.mw-header li:not(.new),
    #mw-panel-toc,
    #mw-navigation li:not(.new),
    #mw-panel li:not(.new),
    #column-one li:not(.new),
    #footer {
      a:not([role="button"], .new) {
        color: @blue !important;
      }
    }

    @media screen {
      html.client-darkmode {
        background-color: @base;
      }
    }

    .vector-feature-zebra-design-disabled,
    body {
      background-color: @base;
    }

