      background-color: @surface0;
    }

    .archwiki-template-meta-related-articles p {
      color: @text;
      border-bottom-color: @blue;
    }

    .archwiki-template-meta-related-articles ul li a {
      color: @blue important;
    }

    .mw-plusminus-neg,
    a.new {
