		#bpage.b_med .infobubble {
			background-color: var(--surface0)
		}

		#bpage.b_med .infobubble.ifbb_tri_top .before,
		#bpage.b_med .infobubble.ifbb_tri_top .after {
			border-bottom-color: var(--surface0)
		}

		#bpage.b_med .infobubble.ifbb_tri_top .before,
		#bpage.b_med .infobubble.ifbb_tri_top .after,
		#bpage.b_med .infobubble.ifbb_tri_bottom .before,
		#bpage.b_med .infobubble.ifbb_tri_bottom .after {
			border-top-color: var(--surface0)
		}
	}

	#bpage.b_drk .infobubble_overlay {
		background-color: #faf9f8
	}

	#bpage.b_drk .infobubble {
		background-color: var(--surface0)
	}

	#bpage.b_drk .infobubble.ifbb_tri_top .before,
	#bpage.b_drk .infobubble.ifbb_tri_top .after {
		border-bottom-color: var(--surface0)
	}

	#bpage.b_drk .infobubble.ifbb_tri_top .before,
	#bpage.b_drk .infobubble.ifbb_tri_top .after,
	#bpage.b_drk .infobubble.ifbb_tri_bottom .before,
	#bpage.b_drk .infobubble.ifbb_tri_bottom .after {
		border-top-color: var(--surface0)
	}

	.infobubble.ifbb_tri_bottom.infobubble_dark .after {
		border-top-color: var(--crust)
	}

	.infobubble.ifbb_tri_top.infobubble_dark .after {
		border-bottom-color: var(--crust)
	}

	.infobubble.ifbb_tri_bottom.infobubble_gray .after {
		border-top-color: var(--surface0)
	}

	.infobubble.ifbb_tri_top.infobubble_gray .after {
		border-bottom-color: var(--surface0)
	}

	.b_dark #sw_as .as_banner {
		background-color: var(--surface0);
		color: var(--text);
	}

	.b_dark #sw_as > * {
		background-color: var(--surface0);
		color: var(--text);
		border: none;
	}

	.b_dark #b_results > li a {
		color: var(--lavender);
	}

	.b_dark #b_results .b_ans.b_nwsAns {
		border: 0;
		background-color: #313244;
	}

	.b_dark #b_results > li.b_ans.b_top.b_topborder {
		background-color: #313244;
	}

	#b_results li.b_ans.b_mop.b_mopb,
	#b_results li.b_ans.b_nonfirsttopb {
		border-radius: 6px;
		box-shadow: 0 0 0 1px rgba(0, 0, 0, .05);
		margin-top: 12px;
		margin-bottom: 10px;
		padding: 15px 19px 10px;
	}
}
